    private void jBEqualsActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jBEqualsActionPerformed
        Calculator calc = new Calculator();
        String[] values = jLDisplay.getText().split(" ");      
        double value1 = Double.parseDouble(values[0]);
        double value2 = Double.parseDouble(values[2]);
        String operator = values[1];
        
        if (values.length == 4 && values[3].equals("%")){
                calc.percentage(value1, value2);
                value2 = calc.getPercent();
        }
        
        switch (operator){
            case "+":
                calc.plus(value1, value2);
                jLDisplay.setText(""+calc.getResult());
                break;
            case "-":
                calc.minus(value1, value2);
                jLDisplay.setText(""+calc.getResult());
                break;
            case "*":
                calc.times(value1, value2);
                jLDisplay.setText(""+calc.getResult());
                break;
            case "/":
                if (value2 == 0){
                    jLDisplay.setText("Error");
                } else {
                    calc.division(value1, value2);
                    jLDisplay.setText(""+calc.getResult());
                }                
                break;
            case "^":
                calc.power(value1, value2);
                jLDisplay.setText(""+calc.getResult());
                break;
            default:
                jLDisplay.setText("Error");
        }
        
    }//GEN-LAST:event_jBEqualsActionPerformed

